create sequence asset_entity_seq start with 1 increment by 50;
create sequence item_entity_seq start with 1 increment by 50;
create sequence summary_entity_seq start with 1 increment by 50;
create table asset_entity (money_value numeric(38,2), id bigint not null, name varchar(255), primary key (id));
create table asset_entity_item_entities (asset_entity_id bigint not null, item_entities_id bigint not null unique);
create table item_entity (money_value numeric(38,2), quantity numeric(20,7), id bigint not null, name varchar(255), primary key (id));
create table password_reset_token (expiry_date timestamp(6), id bigint generated by default as identity, user_id uuid not null unique, token varchar(255), primary key (id));
create table summary_entity (money_value numeric(38,2), state tinyint check (state between 0 and 2), date timestamp(6), id bigint not null, user_id uuid not null, primary key (id));
create table summary_entity_asset_entities (asset_entities_id bigint not null unique, summary_entity_id bigint not null);
create table users (enabled boolean not null, user_id uuid not null, email varchar(255) unique, password varchar(255) not null, username varchar(255) unique, primary key (user_id));
create table verification_tokens (expiry_date timestamp(6), id bigint generated by default as identity, user_id uuid not null, token varchar(255), primary key (id));
alter table if exists asset_entity_item_entities add constraint FKbsyajawmbwgn1e8wlqt3x3unn foreign key (item_entities_id) references item_entity;
alter table if exists asset_entity_item_entities add constraint FK6mksooru95m8e76ynfdqfcm8 foreign key (asset_entity_id) references asset_entity;
alter table if exists password_reset_token add constraint FK83nsrttkwkb6ym0anu051mtxn foreign key (user_id) references users;
alter table if exists summary_entity_asset_entities add constraint FKqkvp3fiaaqjna3s3gckhwap4t foreign key (asset_entities_id) references asset_entity;
alter table if exists summary_entity_asset_entities add constraint FKl11ms5ulltgl1yc4q8fbylk3a foreign key (summary_entity_id) references summary_entity;
alter table if exists verification_tokens add constraint FK54y8mqsnq1rtyf581sfmrbp4f foreign key (user_id) references users;
